import React, { useState, useEffect } from "react";
import { Document, Page, pdfjs} from "react-pdf";
import * as pdfjsLib from "pdfjs-dist";

import { PDFDocumentProxy } from 'pdfjs-dist/types/src/display/api';


pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;


function PdfToJpgConverter() {
  const [file, setFile] = useState<File | null>(null);
  const [pdfData, setPdfData] = useState<string>('');
  const [numPages, setNumPages] = useState(0);

  async function convertToJpg() {
    console.log("convertToJpg");
    const img = new Image();
    
    const pdfDataTest = '';
    
    const decodedPdf = atob(pdfDataTest);
    
    console.log(decodedPdf);
    console.log('pdfjsLib', pdfjsLib);
    const pdf = await pdfjs.getDocument({ data: decodedPdf }).promise;
    
    const pageNumber = 1;
    const page = await pdf.getPage(pageNumber);

    // Set the canvas dimensions to match the PDF page
    const canvas = document.createElement('canvas');
    canvas.width = page.view[2];
    canvas.height = page.view[3];

    // Render the PDF page to the canvas as a JPEG image
    const context = canvas.getContext('2d');
    if (!context || !(context instanceof CanvasRenderingContext2D)) {
      throw new Error('Failed to get 2D context');
    }
    
    const renderContext = {
      canvasContext: context,
      viewport: page.getViewport({ scale: 1 }),
      background: '#fff',
    };
    
    await page.render(renderContext).promise;
    
    const jpegData = canvas.toDataURL('image/jpeg');

    console.log(jpegData);
    
    // // Convert the JPEG data to a Blob object
    console.log(jpegData);
    const byteString = atob(jpegData.split(',')[1]);
    console.log("jpegData.split result")
    console.log(jpegData.split(',')[1]);
    console.log(byteString);
    const mimeType = 'image/jpeg';
    const blob = new Blob([byteString], { type: mimeType });
    
    // // Create a URL for the Blob object and display it in an <img> tag
    const imageUrl = URL.createObjectURL(blob);
    console.log("imageUrl" +imageUrl)

    // img 태그 생성
    const imgElement = document.createElement('img');
    // img 태그 src 속성
    imgElement.src = imageUrl;
    
    document.body.appendChild(imgElement);
    
  }

  const onDocumentLoadSuccess = (pdf : PDFDocumentProxy) => {
    console.log("onDocumentLoadSuccess, numPages");
    console.log(numPages);
    setNumPages(numPages);
  };

  function handleFileChange(event: React.ChangeEvent<HTMLInputElement>) {
    const selectedFile = event.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  }

  useEffect(() => {
    async function loadPdfData() {
      if (file) {
        const dataUrl = await blobToBase64(file);
        console.log(dataUrl);
        setPdfData(dataUrl);
      }
    }
    loadPdfData();
  }, [file]);

  
  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      <img src={pdfData}></img>
      <button onClick={() => convertToJpg()}>변환</button>
    </div>
  );
}

function blobToBase64(blob: Blob): Promise<string> {
  return new Promise<string>((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(blob);
    reader.onloadend = () => {
      const base64Data = reader.result?.toString();
      if (base64Data) {
        resolve(base64Data);

        console.log('base64Data:', base64Data);
      } else {
        reject(new Error('Failed to convert Blob to base64'));
      }
    };
    reader.onerror = () => {
      reject(reader.error);
    };
  });
}

export default PdfToJpgConverter;
